import React from 'react';
import PropTypes from 'prop-types';
import Header from './Header';
import Inventory from './Inventory';
import Order from './Order';
import sampleItems from '../sample-items'
import Item from './Item';
import base from '../base';

class App extends React.Component {
    state = {
        items: {},
        order: {},
        visible: false
    };

    static propTypes = { 
        match: PropTypes.object
    }

    static transitionOptions = {
        classNames: "fish-edit",
        timeout: { enter: 500, exit: 500 }
    }

    componentDidMount() {
        const { params } = this.props.match;
        // first reinstate localStorage
        const localStorageRef = localStorage.getItem(params.storeId)
        if (localStorageRef) {
            this.setState({ order: JSON.parse(localStorageRef) })
        }
        this.ref = base.syncState(`${params.storeId}/fishes`, {
            context: this,
            state: 'fishes'
        });
    }
    componentDidUpdate() {
        localStorage.setItem(this.props.match.params.storeId, JSON.stringify(this.state.order));
    }

    componentWillUnmount() {
        base.removeBinding(this.ref);        
    }

    addItem = (item) => {
        // 1. take a copy of existing state 
        const items = {...this.state.items};
        // 2. add new fish to fishes var
        items[`item${Date.now()}`] = item;
        // 3. set the new fishes object to state
        this.setState({ items });
        // this.setState({
        //     fishes: fishes
        // });
    } 

    updateItem = (key, updatedItem) => {
        const items = {...this.state.items};
        items[key] = updatedItem;
        this.setState({ items })
    }

    deleteItem = (key) => {
        // 1. take a copy of existing state
        const fishes = {...this.state.fishes};
        // 2. set fish to null to delete (firebase)
        fishes[key] = null;
        // 3. update state
        this.setState({ fishes });

    }

    loadSampleItems = () => {
        this.setState({ items: sampleItems });
    }

    addToOrder = (key) => {
        // 1. take a copy of existing state
        const order = {...this.state.order};
        // 2. either add to order or update the order qty
        order[key] = order[key] + 1 || 1;
        // 3. call set state
        this.setState({ order });
    }

    removeFromOrder = (key) => {
        const order = {...this.state.order};
        delete order[key];
        this.setState({ order });
    }

    openAdminPanel = () => {
        this.setState(prev => ({ visible: !prev.visible }))
    }

    render() {        
        return (
            <>
                <div className="catch-of-the-day">
                    <div className="menu">
                        <Header tagline="Love in Every Twist"/>
                        <ul className="fishes">
                            {Object.keys(this.state.items).map(key => (
                                <Item 
                                    key={key} 
                                    index={key}
                                    details={this.state.items[key]} 
                                    addToOrder={this.addToOrder} 
                                />
                            ))}
                        </ul>
                    </div>
                    <Order 
                        items={this.state.items} 
                        order={this.state.order}
                        removeFromOrder={this.removeFromOrder}
                    />
                    <Inventory 
                        visible={this.state.visible}
                        openAdminPanel={this.openAdminPanel}
                        addItem={this.addItem} 
                        updateItem={this.updateItem}
                        deleteItem={this.deleteItem}
                        loadSampleItems={this.loadSampleItems} 
                        items={this.state.items}
                        storeId={this.props.match.params.storeId}
                    />
                </div>
            </>
        );
    }
}

export default App;
